import React from "react";

export default class App extends React.Component {
    constructor() {
        super();
        this.state = {
            num1: 0,
            num2: 0,
            result: null
        };
        this.handleChange = this.handleChange.bind(this);
        this.multiply = this.multiply.bind(this);
    }

    handleChange(event) {
        this.setState({
            [event.target.name]: parseInt(event.target.value)
        });
    }

    multiply() {
        const result = this.state.num1 * this.state.num2;
        this.setState({ result: result });
    }

    render() {
        return (
            <center>
            <div>
                <input type="number" name="num1" value={this.state.num1} onChange={this.handleChange} />
                <br />
                <input type="number" name="num2" value={this.state.num2} onChange={this.handleChange} />
                <br />
                <button onClick={this.multiply}>Multiply</button>
                <br />
                {this.state.result !== null && <p>Result: {this.state.result}</p>}
            </div>
            </center> 
        );
    }
}
