import React, { useState } from "react";
import "./Penjumlahan.css"; // Import file CSS

function Penjumlahan() {
    // State untuk menyimpan nilai kedua angka yang akan dijumlahkan
    const [angka1, setAngka1] = useState(0);
    const [angka2, setAngka2] = useState(0);
    // State untuk menyimpan hasil penjumlahan
    const [hasil, setHasil] = useState(0);

    // Fungsi untuk menangani perubahan nilai angka1
    const handleAngka1Change = (event) => {
        setAngka1(parseInt(event.target.value) || 0); // Mengonversi input menjadi angka, defaultnya 0 jika input tidak valid
    };

    // Fungsi untuk menangani perubahan nilai angka2
    const handleAngka2Change = (event) => {
        setAngka2(parseInt(event.target.value) || 0); // Mengonversi input menjadi angka, defaultnya 0 jika input tidak valid
    };

    // Fungsi untuk menangani penjumlahan
    const handlePenjumlahan = () => {
        const hasilPenjumlahan = angka1 + angka2;
        setHasil(hasilPenjumlahan);
    };

    return (
        <center>
        <div className="penjumlahan-container"> {/* Menggunakan kelas CSS penjumlahan-container */}
            <h2>Penjumlahan Dua Angka</h2>
            <div>
                <label>Angka 1: </label>
                <input type="number" value={angka1} onChange={handleAngka1Change} />
            </div>
            <div>
                <label>Angka 2: </label>
                <input type="number" value={angka2} onChange={handleAngka2Change} />
            </div>
            <button onClick={handlePenjumlahan}>Jumlahkan</button>
            <div>
                <label>Hasil: </label>
                <span>{hasil}</span>
            </div>
        </div>
        </center>
    );
}

export default Penjumlahan;
