import React from "react";

export default class App extends React.Component {
    constructor() {
        super();
        this.state = {
            name: "",
            dateOfBirth: "",
            age: null
        };
        this.handleChange = this.handleChange.bind(this);
        this.calculateAge = this.calculateAge.bind(this);
    }

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    calculateAge() {
        const birthDate = new Date(this.state.dateOfBirth);
        const today = new Date();
        const age = today.getFullYear() - birthDate.getFullYear();
        const month = today.getMonth() - birthDate.getMonth();
        if (month < 0 || (month === 0 && today.getDate() < birthDate.getDate())) {
            age--;
        }
        this.setState({ age: age });
    }

    render() {
        return (
            <div>
                <input type="text" name="name" placeholder="Name" value={this.state.name} onChange={this.handleChange} />
                <br />
                <input type="date" name="dateOfBirth" value={this.state.dateOfBirth} onChange={this.handleChange} />
                <br />
                <button onClick={this.calculateAge}>Calculate Age</button>
                <br />
                {this.state.age !== null && <p>Age: {this.state.age}</p>}
            </div>
        );
    }
}
